import {
  ADDPOST,
  REMOVEPOST,
  EDITPOST,
  VOTE,
  LOADTITLES
} from "../actionTypes";

function makeTitleFromPost(post) {
  return {
    id: post.id,
    title: post.title,
    description: post.description,
    votes: post.votes
  };
}

export default function rootReducer(state = [], action) {
  switch (action.type) {

    case LOADTITLES:
      return [...action.titles];

    case ADDPOST:
      return [...state, makeTitleFromPost(action.post)];

    case EDITPOST:
      titleIdx = state.findIndex(title => title.id === action.payload.id);
      ({ id, description, title, vote } = action.payload.post);

      return [
        ...state.slice(0, titleIdx),
        { id, description, title, vote },
        ...state.slice(titleIdx + 1)
      ];
    default:
      return state;
  }
}
