import React from 'react';

class PostForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = this.props.isEditing ? {
      title: this.props.currentPost.title,
      description: this.props.currentPost.description,
      body: this.props.currentPost.body,
      id: this.props.currentPost.id,
      votes: this.props.currentPost.votes
    } : {
        title: '',
        description: '',
        body: '',
        votes: 0
      };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
  }

  handleSubmit(evt) {
    evt.preventDefault();
    this.props.addPost(this.state);
  }

  handleChange(evt) {
    this.setState({ [evt.target.name]: evt.target.value });
  }

  handleCancel() {
    this.props.cancel();
  }

  async handleEdit(evt) {
    evt.preventDefault();
    await this.props.updatePost(this.state.id, this.state);
  }

  render() {
    let submit;
    this.props.isEditing ? submit = this.handleEdit : submit = this.handleSubmit;

    return (
      <React.Fragment>
        <div className='col-8' style={{ margin: "0 auto" }}>
          <form onSubmit={submit}>
            <div className='form-group'>
              <label htmlFor='title'>Title</label>
              <input
                type='text'
                className='form-control'
                id='title'
                placeholder=''
                name='title'
                value={this.state.title}
                onChange={this.handleChange}
              />
            </div>
            <div className='form-group'>
              <label htmlFor='description'>Description</label>
              <input
                type='text'
                className='form-control'
                id='description'
                placeholder=''
                name='description'
                value={this.state.description}
                onChange={this.handleChange}
              />
            </div>
            <div className='form-group'>
              <label htmlFor='body'>Body</label>
              <textarea
                className='form-control'
                id='body'
                rows='7'
                name='body'
                value={this.state.body}
                onChange={this.handleChange}
              ></textarea>
            </div>
            <button
              type='submit'
              className='btn btn-primary'
              style={{ marginRight: '10px' }}>
              Save
            </button>
          <button
            type='button'
            className='btn btn-secondary'
            onClick={this.handleCancel}
          >
            Cancel
          </button>
          </form>
        </div>
      </React.Fragment>
    )
  }
}

export default PostForm;
